#include <stdio.h>
#include <stdlib.h>
#include <string.h>

char* findDifferentBinaryString(char** nums, int numsSize) {
    int n = numsSize;
    char* res = (char*)malloc((n + 1) * sizeof(char));
    for (int i = 0; i < n; i++) {
        res[i] = (nums[i][i] == '0') ? '1' : '0';
    }
    res[n] = '\0';
    return res;
}

int main() {
    int n;
    if (scanf("%d", &n) != 1) return 0;
    char** nums = (char**)malloc(n * sizeof(char*));
    for (int i = 0; i < n; i++) {
        nums[i] = (char*)malloc((n + 1) * sizeof(char));
        scanf("%s", nums[i]);
    }
    char* ans = findDifferentBinaryString(nums, n);
    printf("%s", ans);
    return 0;
}

