#include <stdio.h>
#include <stdlib.h>

int* smallerNumbersThanCurrent(int* nums, int numsSize, int* returnSize) {
    int* count = (int*)calloc(101, sizeof(int));
    for (int i = 0; i < numsSize; i++) {
        count[nums[i]]++;
    }
    for (int i = 1; i <= 100; i++) {
        count[i] += count[i - 1];
    }
    int* result = (int*)malloc(numsSize * sizeof(int));
    for (int i = 0; i < numsSize; i++) {
        if (nums[i] == 0) result[i] = 0;
        else result[i] = count[nums[i] - 1];
    }
    *returnSize = numsSize;
    free(count);
    return result;
}

int main() {
    int nums[500], n = 0, temp;
    while (scanf("%d", &temp) == 1)
        nums[n++] = temp;

    int returnSize;
    int* result = smallerNumbersThanCurrent(nums, n, &returnSize);

    for (int i = 0; i < returnSize; i++) {
        printf("%d", result[i]);
        if (i < returnSize - 1) printf(" ");
    }
    printf("\n");

    free(result);
    return 0;
}

