#include <stdio.h>
#include <stdlib.h>

int cmpfunc(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}

int* intersection(int* nums1, int nums1Size, int* nums2, int nums2Size, int* returnSize) {
    qsort(nums1, nums1Size, sizeof(int), cmpfunc);
    qsort(nums2, nums2Size, sizeof(int), cmpfunc);

    int *res = (int *)malloc(sizeof(int) * (nums1Size < nums2Size ? nums1Size : nums2Size));
    int i = 0, j = 0, k = 0;

    while (i < nums1Size && j < nums2Size) {
        if (nums1[i] == nums2[j]) {
            if (k == 0 || res[k - 1] != nums1[i]) {
                res[k++] = nums1[i];
            }
            i++;
            j++;
        } else if (nums1[i] < nums2[j]) {
            i++;
        } else {
            j++;
        }
    }

    *returnSize = k;
    return res;
}

int main() {
    int nums1[1000], nums2[1000];
    int n1 = 0, n2 = 0, val;

    while (scanf("%d", &val) == 1) {
        nums1[n1++] = val;
        if (getchar() == '\n') break;
    }
    while (scanf("%d", &val) == 1) {
        nums2[n2++] = val;
        if (getchar() == '\n') break;
    }

    int size;
    int *res = intersection(nums1, n1, nums2, n2, &size);

    for (int i = 0; i < size; i++) {
        if (i > 0) printf(" ");
        printf("%d", res[i]);
    }

    free(res);
    return 0;
}

