import java.util.*;

class Solution {
    public int[] intersection(int[] nums1, int[] nums2) {
        Set<Integer> set1 = new HashSet<>();
        for (int x : nums1) {
            set1.add(x);
        }
        Set<Integer> resultSet = new HashSet<>();
        for (int x : nums2) {
            if (set1.contains(x)) {
                resultSet.add(x);
            }
        }
        int[] res = new int[resultSet.size()];
        int i = 0;
        for (int x : resultSet) {
            res[i++] = x;
        }
        return res;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        if (!sc.hasNextLine()) return;
        String line1 = sc.nextLine().trim();
        if (!sc.hasNextLine()) return;
        String line2 = sc.nextLine().trim();

        String[] arr1 = line1.isEmpty() ? new String[0] : line1.split(" ");
        String[] arr2 = line2.isEmpty() ? new String[0] : line2.split(" ");

        int[] nums1 = new int[arr1.length];
        int[] nums2 = new int[arr2.length];

        for (int i = 0; i < arr1.length; i++) {
            nums1[i] = Integer.parseInt(arr1[i]);
        }
        for (int i = 0; i < arr2.length; i++) {
            nums2[i] = Integer.parseInt(arr2[i]);
        }

        Solution s = new Solution();
        int[] res = s.intersection(nums1, nums2);

        for (int i = 0; i < res.length; i++) {
            if (i > 0) System.out.print(" ");
            System.out.print(res[i]);
        }
    }
}

