#include <stdio.h>
#include <stdlib.h>
#include <stdbool.h>

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
bool* kidsWithCandies(int* candies, int candiesSize, int extraCandies, int* returnSize) {
    int max = 0;
    for (int i = 0; i < candiesSize; i++) {
        if (candies[i] > max) max = candies[i];
    }
    bool* result = (bool*)malloc(sizeof(bool) * candiesSize);
    for (int i = 0; i < candiesSize; i++) {
        result[i] = (candies[i] + extraCandies >= max);
    }
    *returnSize = candiesSize;
    return result;
}

void printResult(bool* result, int size) {
    printf("[");
    for (int i = 0; i < size; i++) {
        printf(result[i] ? "true" : "false");
        if (i != size - 1) printf(",");
    }
    printf("]\n");
}

int main() {
    int n;
    scanf("%d", &n);
    int* candies = (int*)malloc(sizeof(int) * n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &candies[i]);
    }
    int extraCandies;
    scanf("%d", &extraCandies);
    int returnSize;
    bool* result = kidsWithCandies(candies, n, extraCandies, &returnSize);
    printResult(result, returnSize);
    free(candies);
    free(result);
    return 0;
}

