#include <iostream>
#include <vector>
using namespace std;

vector<bool> kidsWithCandies(vector<int>& candies, int extraCandies) {
    int maxCandies = 0;
    for (int c : candies) {
        if (c > maxCandies) maxCandies = c;
    }
    vector<bool> result;
    result.reserve(candies.size());
    for (int c : candies) {
        result.push_back(c + extraCandies >= maxCandies);
    }
    return result;
}

void printResult(const vector<bool>& result) {
    cout << "[";
    for (size_t i = 0; i < result.size(); ++i) {
        cout << (result[i] ? "true" : "false");
        if (i != result.size() - 1) cout << ",";
    }
    cout << "]" << endl;
}

int main() {
    int n;
    cin >> n;
    vector<int> candies(n);
    for (int i = 0; i < n; ++i) cin >> candies[i];
    int extraCandies;
    cin >> extraCandies;
    vector<bool> result = kidsWithCandies(candies, extraCandies);
    printResult(result);
    return 0;
}

